<script>
	const body = document.querySelector('body');
	const staticDimmer = document.querySelector('.static-dimmer');
	const staticMenu = document.querySelector('.static-menu');
	const mainToggle = document.querySelector('.static-menu-toggle');
	const infoToggle = document.querySelector('.static-menu__info-toggle');

	function menuSwitch() {

		// Close
		if (body.classList.contains('static-menu-open')) {
			body.classList.remove('static-menu-open');
			setTimeout(function() {
				body.classList.remove('dimmer-visual');
			}, 480);
			staticMenu.classList.remove('info-page-open');
		} else {
		// Open
			body.classList.add('dimmer-visual');
			setTimeout(function() {
				body.classList.add('static-menu-open');
			}, 1);
		}
	}

	function infoSwitch() {
		// Close
		if (staticMenu.classList.contains('info-page-open')) {
			staticMenu.classList.remove('info-page-open');
			setTimeout(function() {
				staticMenu.classList.remove('info-page-visual');
			}, 480);
		} else {
		// Open
			staticMenu.classList.add('info-page-visual');
			setTimeout(function() {
				staticMenu.classList.add('info-page-open');
			}, 1);
		}
	}
	mainToggle.addEventListener('click', function(){menuSwitch()});
	staticDimmer.addEventListener('click', function(){menuSwitch()});
	infoToggle.addEventListener('click', function(){infoSwitch()});
</script>
